import { Component, OnInit } from '@angular/core';
import { TaskService } from '../task.service';
import { Task } from '../models/task';  // Assure-toi que ton modèle Task est correctement importé

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css']
})
export class TaskListComponent implements OnInit {
  tasks: Task[] = [];
  newTask: Task = { title: '', description: '', completed: false, status: 'PENDING' }; // Initialisation d'une tâche vide

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
    this.taskService.getTasks().subscribe((data: Task[]) => {
      this.tasks = data;
    });
  }

  // Méthode pour ajouter une nouvelle tâche
  addTask(): void {
    this.taskService.addTask(this.newTask).subscribe((task: Task) => {
      this.tasks.push(task);  // Ajoute la tâche ajoutée au tableau des tâches
      this.newTask = { title: '', description: '', completed: false, status: 'PENDING' };  // Réinitialise le formulaire
    });
  }
}
