import { Component, OnInit } from '@angular/core';
import { TaskService } from '../task.service';
import { Task } from '../models/task';  // Assure-toi que ton modèle Task est correctement importé

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css']
})
export class TaskListComponent implements OnInit {
  tasks: Task[] = [];
  newTask: Task = { title: '', description: '', completed: false, status: 'PENDING' }; // Initialisation d'une tâche vide

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
    this.taskService.getTasks().subscribe((data: Task[]) => {
      this.tasks = data;
    });
  }

  // Méthode pour ajouter une nouvelle tâche
  addTask(): void {
    this.taskService.addTask(this.newTask).subscribe((task: Task) => {
      this.tasks.push(task);  // Ajoute la tâche ajoutée au tableau des tâches
      this.newTask = { title: '', description: '', completed: false, status: 'PENDING' };  // Réinitialise le formulaire
    });
  }
  deleteTask(id: number | undefined): void {
    if (id !== undefined) {  // Vérifie si l'id est défini
      this.taskService.deleteTask(id).subscribe(() => {
        this.tasks = this.tasks.filter(task => task.id !== id);  // Retirer la tâche de la liste après suppression
      });
    } else {
      console.error("ID is undefined");
    }
  }

  // Mettre à jour une tâche
  updateTask(id: number | undefined, updatedTask: Task): void {
    if (id !== undefined) {  // Vérifie si l'id est défini
      this.taskService.updateTask(id, updatedTask).subscribe((task) => {
        const index = this.tasks.findIndex(t => t.id === task.id);
        if (index !== -1) {
          this.tasks[index] = task;  // Remplacer la tâche dans la liste
        }
      });
    } else {
      console.error("ID is undefined");
    }}
    finishTask(task: Task): void {
      if (task.id != null) {
        const updatedTask = { ...task, status: 'COMPLETED' };
        this.taskService.updateTask(task.id, updatedTask).subscribe((updatedTask) => {
          const index = this.tasks.findIndex(t => t.id === updatedTask.id);
          if (index !== -1) {
            this.tasks[index] = updatedTask;  // Mettre à jour la tâche dans la liste
          }
        });
      } else {
        console.error('Task ID is missing');
      }
    }

}
